import { getPaymentInfoAPI } from '@/api/paymentAuditAPI.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 规格列表
      pgid: '',
      dataForm: {},
      imageShowUrl: '' // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    paymentId: {
      required: true
    }
  },
  computed: {},
  methods: {
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },
    // 获取打款详情
    getPaymentDetail: function getPaymentDetail(paymentId) {
      var _this = this;

      this.wholeLoding = true;
      getPaymentInfoAPI(paymentId).then(function (res) {
        _this.dataForm = res.data.data;
        _this.dataForm.piclist = [];
        _this.dataForm.piclist = _this.dataForm.paymentVoucher.split(',');
        console.log(_this.dataForm);
      }).catch(function (err) {
        _this.wholeLoding = false;
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    this.getPaymentDetail(this.paymentId);
    console.log(this.paymentId);
  }
};
