import QRCode from 'qrcodejs2';
import Clipboard from 'clipboard';
import { timeStamp2String } from '@/api/globalAPI.js';
export default {
  name: 'prodList',
  data: function data() {
    return {
      isDiscount: '0',
      agentid: '',
      agentLevelList: [{
        name: '董事',
        value: 1
      }, {
        name: '钻石',
        value: 2
      }, {
        name: '省代',
        value: 3
      }, {
        name: '市代',
        value: 4
      }, {
        name: 'VIP399套餐',
        value: 5
      }],
      dataForm: {
        checkList: [],
        url: 'http://ws.zhanchengwlkj.com/zhuce.html?id=0'
      },
      timelist: [],
      stock_type: 1
    };
  },
  components: {// AddDepModal,
  },
  props: {},
  methods: {
    /**
     * @msg: 时间处理
     * @param {type} 
     * @return: 
     */
    timechange: function timechange(time, type) {
      return timeStamp2String(time, type);
    },
    copy: function copy() {
      var _this = this;

      var clipboard = new Clipboard('.tag-read');
      clipboard.on('success', function (e) {
        _this.$message({
          type: 'success',
          message: '复制成功'
        }); // 释放内存


        clipboard.destroy();
      });
      clipboard.on('error', function (e) {
        // 不支持复制
        console.log('该浏览器不支持自动复制'); // 释放内存

        clipboard.destroy();
      });
    },
    dataFormSubmit: function dataFormSubmit() {
      var _this2 = this;

      var that = this;
      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          var params = _this2.dataForm;
          console.log(params);
        }
      });
    },
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },
    codeshow: function codeshow() {
      new QRCode(this.$refs.qrCodeDiv, {
        text: 'http://ws.zhanchengwlkj.com/zhuce.html?id=0',
        width: 200,
        height: 200,
        colorDark: "#333333",
        //二维码颜色
        colorLight: "#ffffff",
        //二维码背景色
        correctLevel: QRCode.CorrectLevel.L //容错率，L/M/H

      });
    },
    downcodes: function downcodes() {
      var myCanvas = document.getElementById('qrcode').getElementsByTagName('canvas');
      var img = document.getElementById('qrcode').getElementsByTagName('img'); // // //创建一个a标签节点

      var a = document.createElement("a"); // //设置a标签的href属性（将canvas变成png图片）

      var imgURL = myCanvas[0].toDataURL('image/jpg');
      var ua = navigator.userAgent;

      if (ua.indexOf("Trident") != -1 && ua.indexOf("Windows") != -1) {
        // IE内核 并且  windows系统 情况下 才执行;
        var bstr = atob(imgURL.split(',')[1]);
        var n = bstr.length;
        var u8arr = new Uint8Array(n);

        while (n--) {
          u8arr[n] = bstr.charCodeAt(n);
        }

        var blob = new Blob([u8arr]);
        window.navigator.msSaveOrOpenBlob(blob, '下载' + '.' + 'png');
      } else if (ua.indexOf("Firefox") > -1) {
        //火狐兼容下载
        var _blob = this.base64ToBlob(imgURL); //new Blob([content]);


        var evt = document.createEvent("HTMLEvents");
        evt.initEvent("click", true, true); //initEvent 不加后两个参数在FF下会报错  事件类型，是否冒泡，是否阻止浏览器的默认行为

        a.download = ' '; //下载图片名称，如果填内容识别不到，下载为未知文件，所以我这里就不填为空

        a.href = URL.createObjectURL(_blob);
        a.dispatchEvent(new MouseEvent('click', {
          bubbles: true,
          cancelable: true,
          view: window
        })); //兼容火狐
      } else {
        //谷歌兼容下载
        img.src = myCanvas[0].toDataURL('image/jpg'); // a.href = myCanvas[0].toDataURL('image/png').replace('image/png', 'image/octet-stream')

        a.href = img.src; //设置下载文件的名字

        a.download = "下载"; //点击

        a.click();
      }
    }
  },
  mounted: function mounted() {
    this.codeshow();
  }
};
