import { getAgentDetailAPI, changeyeAPI } from '@/api/agentInformationApi.js';
export default {
  name: 'editor',
  data: function data() {
    var _this = this;

    var validatePass = function validatePass(rule, value, callback) {
      console.log(_this.dataForm.num);

      if (Number(_this.dataForm.num) > Number(_this.allnum) && _this.dataForm.changeType == 2) {
        callback(new Error('调整数目不能大于余额'));
      } else {
        callback();
      }
    };

    return {
      // 规格列表
      Stocktype: [{
        val: 1,
        title: '增加积分'
      }, {
        val: 2,
        title: '减少积分'
      }],
      allnum: '0',
      rules: {
        num: [{
          required: true,
          message: '不能为空',
          trigger: ['blur', 'change']
        }, {
          required: true,
          trigger: 'blur',
          validator: validatePass
        }]
      },
      changeid: '',
      userdetial: {},
      dataForm: {
        changeType: 1,
        num: ''
      } // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    agentId: {
      required: true
    }
  },
  computed: {
    defalutSku: function defalutSku() {
      return this.$store.state.prod.defalutSku;
    }
  },
  methods: {
    dataFormSubmit: function dataFormSubmit() {
      var _this2 = this;

      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          var params = {
            agentId: _this2.changeid,
            adminId: sessionStorage.getItem('uid'),
            changeType: _this2.dataForm.changeType,
            amount: _this2.dataForm.num
          };
          changeyeAPI(params).then(function (res) {
            var response = res.data;
            console.log(response);

            if (response.code == 200) {
              _this2.$message({
                type: 'success',
                message: '修改积分成功'
              });

              _this2.$emit('close-form');

              _this2.$emit('finish-action');
            } else {
              _this2.errorMsg(response.message);
            }
          }).catch(function (err) {
            _this2.tableLoading = false;
          });
        }
      });
    },
    getAgentDetail: function getAgentDetail(id) {
      var _this3 = this;

      console.log(id);
      getAgentDetailAPI(id).then(function (res) {
        var data = res.data.data;
        _this3.allnum = data.incomeBalance;
        _this3.userdetial = data;
        console.log(data);
      }).catch(function (err) {
        _this3.tableLoading = false;
      });
    },
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    this.getAgentDetail(this.agentId);
    this.changeid = this.agentId;
  }
};
