import { addFreightAPI, getFrieghtListAPI, putFreightAPI } from '@/api/prodFreightAPI.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 运费模板
      freightId: '0',
      checked: false,
      tableData: [{
        firstNumber: '',
        firstPrice: '',
        renewNumber: '',
        isLarge: 0,
        type: 1,
        renewPrice: ''
      }],
      tableDatatwo: [{
        firstNumber: '',
        firstPrice: '',
        renewNumber: '',
        isLarge: '',
        type: 1,
        renewPrice: ''
      }],
      checkList: [],
      shopup: '',
      dataForm: {
        text: '',
        title: '',
        valuationWay: '1',
        transportWay: '' // domains: [],

      },
      imageShowUrl: '' // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    prodgroupId: {
      required: true
    }
  },
  computed: {},
  methods: {
    /*
      * 获取添加运费模板
      */
    pushGroupFormData: function pushGroupFormData() {
      var _this = this;

      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          var pushdata = {
            title: _this.dataForm.title,
            transportWay: _this.dataForm.transportWay,
            valuationWay: Number(_this.dataForm.valuationWay),
            defaultBuyins: 0,
            defaultRetail: 0,
            defaultStock: 0,
            defaultTake: 0,
            freightRegionList: []
          };

          for (var i = 0; i < _this.checkList.length; i++) {
            if (_this.checkList[i] == "采购订单") {
              pushdata.defaultStock = 1;
            } else if (_this.checkList[i] == "内购订单") {
              pushdata.defaultBuyins = 1;
            } else if (_this.checkList[i] == "提货订单") {
              pushdata.defaultTake = 1;
            } else {
              pushdata.defaultRetail = 1;
            }
          }

          pushdata.freightRegionList.push(_this.tableData[0]);

          if (_this.checked) {
            pushdata.freightRegionList.push(_this.tableDatatwo[0]);
          }

          if (_this.freightId != 0) {
            pushdata.id = _this.freightId;
            putFreightAPI(pushdata).then(function (res) {
              var response = res.data;

              _this.$message({
                type: 'success',
                message: '修改成功'
              });

              _this.$emit('close-form');

              _this.$emit('finish-action');
            }).catch(function (err) {
              console.log(err);
              _this.tableLoading = false;
            });
          } else {
            addFreightAPI(pushdata).then(function (res) {
              var response = res.data;

              _this.$message({
                type: 'success',
                message: '添加成功'
              });

              _this.$emit('close-form');

              _this.$emit('finish-action');
            }).catch(function (err) {
              console.log(err);
              _this.tableLoading = false;
            });
          }
        }
      });
    },
    getFreightDetail: function getFreightDetail(id) {
      var _this2 = this;

      getFrieghtListAPI(id).then(function (res) {
        var response = res.data.data;
        _this2.dataForm.title = response.title;
        _this2.dataForm.transportWay = response.transportWay;
        _this2.dataForm.valuationWay = '' + response.valuationWay;

        if (response.defaultStock == 1) {
          _this2.checkList.push('采购订单');
        }

        if (response.defaultBuyins == 1) {
          _this2.checkList.push('内购订单');
        }

        if (response.defaultTake == 1) {
          _this2.checkList.push('提货订单');
        }

        if (response.defaultRetail == 1) {
          _this2.checkList.push('零售订单');
        }

        if (response.freightRegionList.length > 1) {
          _this2.tableData = [];

          _this2.tableData.push(response.freightRegionList[0]);

          _this2.tableDatatwo = [];

          _this2.tableDatatwo.push(response.freightRegionList[1]);
        } else {
          _this2.tableData = [];

          _this2.tableData.push(response.freightRegionList[0]);
        }
      }).catch(function (err) {
        console.log(err);
        _this2.tableLoading = false;
      });
    },
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    if (this.prodgroupId != 0) {
      this.getFreightDetail(this.prodgroupId);
      this.freightId = this.prodgroupId;
    }

    console.log(this.prodgroupId);
  }
};
