      getOrderDepListAPI(params).then(function (res) {
        var data = res.data.data;
        _this2.totalCount = data.total;
        _this2.tableData = data.records;
        _this2.tableLoading = false;
        var nowprodnum = data.count;
        _this2.shoptypelist[0].nownum = nowprodnum.putawayNum;
        _this2.shoptypelist[1].nownum = nowprodnum.sellOutNum;
        _this2.shoptypelist[2].nownum = nowprodnum.soldOutNum;
        _this2.shoptypelist[3].nownum = nowprodnum.warningNum;
      }).catch(function (err) {
