import { getFranchiseDetailAPI, pushFranchiseDetailAPI, putFranchiseDetailAPI } from '@/api/franchiseStoreAdministrators.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 规格列表
      dataForm: {
        username: '',
        pwd: '',
        roleId: 3,
        nickname: '',
        cover: '',
        phone: ''
      },
      ispwdshow: 0,
      imageShowUrl: '' // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    franchiseId: {
      required: true
    }
  },
  computed: {
    defalutSku: function defalutSku() {
      return this.$store.state.prod.defalutSku;
    }
  },
  activated: function activated() {
    this.dataForm.prodId = this.$route.query.prodId;
    this.getDataList();
  },
  methods: {
    uploadImg: function uploadImg(res) {
      this.dataForm.cover = res.data;
      this.imageShowUrl = res.data;
    },
    beforeAvatarUpload: function beforeAvatarUpload(file) {
      var isJPG = file.type === 'image/jpeg' || file.type === 'image/png';
      var isLt2M = file.size / 1024 / 1024 < 2;

      if (!isJPG) {
        this.$message.error('上传头像图片只能是 JPG或者PNG 格式!');
      }

      if (!isLt2M) {
        this.$message.error('上传头像图片大小不能超过 2MB!');
      }

      return isJPG && isLt2M;
    },
    dataFormSubmit: function dataFormSubmit() {
      var _this = this;

      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          var params = {
            username: _this.dataForm.username,
            trueName: _this.dataForm.trueName,
            roleId: 3,
            nickname: _this.dataForm.nickname,
            cover: _this.dataForm.cover,
            phone: _this.dataForm.phone
          };

          if (_this.ispwdshow == 0) {
            params.pwd = _this.dataForm.pwd;
            pushFranchiseDetailAPI(params).then(function (res) {
              var sucresponse = res.data;

              if (sucresponse.code == 200) {
                _this.$message({
                  type: 'success',
                  message: '添加管理员信息成功'
                });

                _this.$emit('close-form');

                _this.$emit('finish-action');
              } else {
                _this.errorMsg(sucresponse.message);
              }
            }).catch(function (err) {
              console.log(err);
              _this.tableLoading = false;
            });
          } else {
            params.uid = _this.dataForm.uid;
            putFranchiseDetailAPI(params).then(function (res) {
              var sucresponse = res.data;

              if (sucresponse.code == 200) {
                _this.$message({
                  type: 'success',
                  message: '修改管理员信息成功'
                });

                _this.$emit('close-form');

                _this.$emit('finish-action');
              } else {
                _this.errorMsg(sucresponse.message);
              }
            }).catch(function (err) {
              console.log(err);
              _this.tableLoading = false;
            });
          }

          console.log(params);
        }
      });
    },
    getoneTypeList: function getoneTypeList(id) {},
    getFranchiseDetail: function getFranchiseDetail(id) {
      var _this2 = this;

      var params = id;
      getFranchiseDetailAPI(params).then(function (res) {
        console.log(res);
        var data = res.data.data;
        _this2.dataForm = data;
        _this2.imageShowUrl = data.cover;
      }).catch(function (err) {
        _this2.tableLoading = false;
      });
    },
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    if (this.franchiseId != 0) {
      this.getFranchiseDetail(this.franchiseId);
      this.ispwdshow = this.franchiseId;
    }

    console.log(this.franchiseId);
  }
};
