export default {
  name: '',
  data: function data() {
    return {
      lineChart: null
    };
  },
  props: {
    titleText: {
      type: String
    },
    xAxisData: {
      required: true,
      type: Array
    },
    seriesData: {
      required: true,
      type: Array
    }
  },
  watch: {
    seriesData: function seriesData() {
      this.setOption();
    }
  },
  methods: {
    /**
     * 初始化图表
     */
    initChart: function initChart() {
      var dom = this.$refs.lineChart;
      this.lineChart = this.$echarts.init(dom);
      this.setOption();
    },

    /**
     * 设置图表参数
     */
    setOption: function setOption() {
      this.lineChart.setOption({
        title: {
          text: this.titleText || '',
          left: 'middle'
        },
        tooltip: {
          trigger: 'axis'
        },
        xAxis: {
          type: 'category',
          data: this.xAxisData,
          axisTick: {
            alignWithLabel: true
          }
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          data: this.seriesData,
          type: 'line'
        }]
      });
    }
  },
  mounted: function mounted() {
    this.initChart();
  }
};
