var PasswordEdit = function PasswordEdit() {
  return import('./PasswordEdit.vue');
};

import moment from 'moment';
export default {
  name: "",
  data: function data() {
    return {
      dateCounter: moment(Date.now()).format('YYYY-MM-DD HH:mm:ss'),
      // 时间初始化
      username: sessionStorage.getItem('username'),
      modelShow: false
    };
  },
  components: {
    PasswordEdit: PasswordEdit
  },
  methods: {
    /**
     * 用户操作下拉菜单选择
     */
    selectUserOption: function selectUserOption(command) {
      switch (command) {
        case 'password':
          this.modelShow = true;
          break;

        case 'logout':
          this.logoutSystem();
          break;

        default:
          break;
      }
    },

    /**
     * 登出操作
     */
    logoutSystem: function logoutSystem() {
      this.$store.commit('moduleLogin/logout');
      this.$router.push('/login');
    }
  },
  mounted: function mounted() {
    var _this = this;

    // 系统时间显示
    this.timer = setInterval(function () {
      _this.dateCounter = moment(Date.now()).format('YYYY-MM-DD HH:mm:ss');
    }, 1000);
  },
  beforeDestroy: function beforeDestroy() {
    clearInterval(this.timer);
  }
};
