import { getAdminRoleInfoAPI, pushAdminRoleInfoAPI, putAdminRoleInfoAPI } from '@/api/adminRoleApi.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 规格列表
      dataForm: {
        roleName: '',
        note: ''
      }
    };
  },
  components: {},
  props: {
    updateId: {
      required: true
    }
  },
  computed: {
    defalutSku: function defalutSku() {
      return this.$store.state.prod.defalutSku;
    }
  },
  activated: function activated() {
    this.dataForm.prodId = this.$route.query.prodId;
    this.getDataList();
  },
  methods: {
    dataFormSubmit: function dataFormSubmit() {
      var _this = this;

      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          var params = {
            roleName: _this.dataForm.roleName,
            note: _this.dataForm.note
          };

          if (_this.updateId == 0) {
            pushAdminRoleInfoAPI(params).then(function (res) {
              var sucresponse = res.data;

              if (sucresponse.code == 200) {
                _this.$message({
                  type: 'success',
                  message: '添加成功'
                });

                _this.$emit('close-form');

                _this.$emit('finish-action');
              } else {
                _this.errorMsg(sucresponse.message);
              }
            }).catch(function (err) {
              console.log(err);
              _this.tableLoading = false;
            });
          } else {
            params.id = _this.updateId;
            putAdminRoleInfoAPI(params).then(function (res) {
              var sucresponse = res.data;

              if (sucresponse.code == 200) {
                _this.$message({
                  type: 'success',
                  message: '修改成功'
                });

                _this.$emit('close-form');

                _this.$emit('finish-action');
              } else {
                _this.errorMsg(sucresponse.message);
              }
            }).catch(function (err) {
              console.log(err);
              _this.tableLoading = false;
            });
          }

          console.log(params);
        }
      });
    },
    getDetail: function getDetail(id) {
      var _this2 = this;

      var params = id;
      getAdminRoleInfoAPI(params).then(function (res) {
        console.log(res);
        var data = res.data.data;
        _this2.dataForm = data;
      }).catch(function (err) {
        _this2.tableLoading = false;
      });
    },
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    if (this.updateId != 0) {
      this.getDetail(this.updateId);
    }

    console.log(this.updateId);
  }
};
