import { getPayeeAccountsInfoAPI, updatePayeeAccountsInfoAPI } from '@/api/financeAPI.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 规格列表
      pgid: '',
      options: [{
        value: 1,
        label: '银行卡'
      }, {
        value: 2,
        label: '支付宝'
      }, {
        value: 3,
        label: '微信'
      }],
      dataForm: {
        title: '',
        domains: [],
        type: '',
        cover: ''
      },
      imageShowUrl: '' // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    financeId: {
      required: true
    }
  },
  computed: {},
  methods: {
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },
    uploadImg: function uploadImg(res) {
      this.dataForm.qrCode = res.data;
      this.imageShowUrl = res.data;
    },
    beforeAvatarUpload: function beforeAvatarUpload(file) {
      var isJPG = file.type === 'image/jpeg' || file.type === 'image/png';
      var isLt2M = file.size / 1024 / 1024 < 2;

      if (!isJPG) {
        this.$message.error('上传头像图片只能是 JPG或者PNG 格式!');
      }

      if (!isLt2M) {
        this.$message.error('上传头像图片大小不能超过 2MB!');
      }

      return isJPG && isLt2M;
    },
    getFinanceSetUpInfo: function getFinanceSetUpInfo(id) {
      var _this = this;

      getPayeeAccountsInfoAPI(id).then(function (res) {
        console.log(res);
        var data = res.data.data;
        _this.dataForm = data;
        console.log(data);
      }).catch(function (err) {
        _this.tableLoading = false;
      });
    },
    dataFormSubmit: function dataFormSubmit() {
      var _this2 = this;

      var that = this;
      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          var putdata = {
            id: _this2.dataForm.id,
            type: _this2.dataForm.type,
            trueName: _this2.dataForm.trueName,
            depositBank: _this2.dataForm.depositBank,
            qrCode: _this2.dataForm.qrCode
          };
          updatePayeeAccountsInfoAPI(putdata).then(function (res) {
            var sucresponse = res.data;

            if (sucresponse.code == 200) {
              _this2.$message({
                type: 'success',
                message: '修改账户成功'
              });

              _this2.$emit('close-form');

              _this2.$emit('finish-action');
            } else {
              _this2.errorMsg(sucresponse.message);
            }
          }).catch(function (err) {
            console.log(err);
            _this2.tableLoading = false;
          });
        }
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    console.log(this.financeId);

    if (this.financeId != 0) {
      // this.pgid=this.prodgroupId;
      this.getFinanceSetUpInfo(this.financeId); // this.getGroupDetail(this.prodgroupId)
    }
  }
};
