import { getProdDetailAPI, changeProdDetilAPI, changeSkuDetilAPI } from '@/api/productStockApi.js';
export default {
  name: 'editor',
  data: function data() {
    var _this = this;

    var validatePass = function validatePass(rule, value, callback) {
      console.log(_this.dataForm.num);

      if (Number(_this.dataForm.num) > Number(_this.allnum) && _this.dataForm.changeType == 2) {
        callback(new Error('调整数目不能大于库存'));
      } else {
        callback();
      }
    };

    return {
      // 规格列表
      Stocktype: [{
        val: 1,
        title: '增加库存'
      }, {
        val: 2,
        title: '减少库存'
      }],
      allnum: '0',
      prodName: '',
      cover: '',
      rules: {
        num: [{
          required: true,
          message: '不能为空',
          trigger: ['blur', 'change']
        }, {
          required: true,
          trigger: 'blur',
          validator: validatePass
        }]
      },
      skuId: '',
      dataForm: {
        pid: '',
        changeType: 1,
        userType: 1,
        operatorId: 1,
        remark: '',
        num: ''
      } // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    prodId: {
      required: true
    },
    prodNum: {
      required: true
    },
    prodSku: {
      required: true
    }
  },
  computed: {
    defalutSku: function defalutSku() {
      return this.$store.state.prod.defalutSku;
    }
  },
  methods: {
    dataFormSubmit: function dataFormSubmit() {
      var _this2 = this;

      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          var params = _this2.dataForm;

          if (_this2.skuId != '') {
            console.log('修改sku库存');
            var pushparams = {
              pid: params.pid,
              changeType: params.changeType,
              remark: params.remark,
              userType: 1,
              operatorId: 1,
              skuStockDtos: [{
                skuId: _this2.skuId,
                num: Number(params.num)
              }]
            };
            changeSkuDetilAPI(pushparams).then(function (res) {
              var response = res.data;
              console.log(response);

              if (response.code == 200) {
                _this2.$message({
                  type: 'success',
                  message: '修改库存成功'
                });

                _this2.$emit('close-form');

                _this2.$emit('finish-action');
              } else {
                _this2.errorMsg(response.message);
              }
            }).catch(function (err) {
              _this2.tableLoading = false;
            });
          } else {
            params.num = Number(params.num);
            changeProdDetilAPI(params).then(function (res) {
              var response = res.data;

              if (response.code == 200) {
                _this2.$message({
                  type: 'success',
                  message: '修改库存成功'
                });

                _this2.$emit('close-form');

                _this2.$emit('finish-action');
              } else {
                errorMsg(response.message);
              }
            }).catch(function (err) {
              _this2.tableLoading = false;
            });
          }
        }
      });
    },
    getProdDetail: function getProdDetail(id) {
      var _this3 = this;

      getProdDetailAPI(id).then(function (res) {
        var data = res.data.data;
        _this3.prodName = data.productName;
        _this3.cover = data.cover;
        console.log(data);
      }).catch(function (err) {
        _this3.tableLoading = false;
      });
    },
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    this.dataForm.pid = this.prodId;
    this.getProdDetail(this.prodId);
    this.allnum = this.prodNum;

    if (this.prodSku != 0) {
      this.skuId = this.prodSku;
    }

    console.log(this.prodSku);
    console.log(this.prodNum);
  }
};
