var LineChart = function LineChart() {
  return import('../charts/LineChart.vue');
};

var PieChart = function PieChart() {
  return import('../charts/PieChart.vue');
};

import { getDoctorCountAPI, getDoctorDataAPI } from '@/api/homeAPI.js';
export default {
  name: '',
  data: function data() {
    return {
      loading: true,
      year: 2018,
      hospitalClass: '三甲',
      lineXAxisData: [],
      lineData: [],
      // 饼图数据
      signData: [],
      // 签约占比
      doctorClassData: [],
      // 医生占比
      departmentData: [] // 科室医生占比

    };
  },
  components: {
    LineChart: LineChart,
    PieChart: PieChart
  },
  methods: {
    /**
     * 返回首页
     */
    backOverview: function backOverview() {
      this.$emit('back-overview', '');
    },

    /**
     * 获取医生折线图数据
     */
    getDoctorCount: function getDoctorCount() {
      var _this = this;

      return getDoctorCountAPI({
        year: this.year,
        hospitalClass: this.hospitalClass
      }).then(function (res) {
        var data = res.data.data;
        var lineXAxisData = [];
        var lineData = [];
        data.forEach(function (item) {
          lineXAxisData.push("".concat(item.month, "\u6708"));
          lineData.push(item.doctorNum);
        });
        _this.lineXAxisData = lineXAxisData;
        _this.lineData = lineData;
      });
    },

    /**
     * 获取医生数据饼图数据
     */
    getDoctorData: function getDoctorData() {
      var _this2 = this;

      return getDoctorDataAPI({
        year: this.year,
        hospitalClass: this.hospitalClass
      }).then(function (res) {
        var data = res.data.data;
        _this2.signData = data.sign;
        _this2.doctorClassData = data.doctorClass;
        _this2.departmentData = data.department;
      });
      ;
    },

    /**
     * 根据筛选选择器获取新数据
     */
    filtData: function filtData() {
      var _this3 = this;

      this.loading = true;
      Promise.all([this.getDoctorCount(), this.getDoctorData()]).then(function (res) {
        _this3.loading = false;
      }).catch(function (err) {
        _this3.loading = false;
      });
    }
  },
  created: function created() {
    this.filtData();
  }
};
