export default {
  name: '',
  data: function data() {
    return {
      lineChart: null
    };
  },
  props: {
    titleText: {
      required: true,
      type: String
    },
    pieData: {
      required: true,
      type: Array
    }
  },
  watch: {
    pieData: function pieData() {
      this.setOption();
    }
  },
  methods: {
    /**
     * 初始化图表
     */
    initChart: function initChart() {
      var dom = this.$refs.lineChart;
      this.lineChart = this.$echarts.init(dom);
      this.setOption();
    },

    /**
     * 设置图表参数
     */
    setOption: function setOption() {
      this.lineChart.setOption({
        title: {
          text: this.titleText,
          x: 'center'
        },
        tooltip: {
          trigger: 'item',
          formatter: "{a} <br/>{b} : {c} ({d}%)"
        },
        legend: {
          orient: 'vertical',
          left: 'left',
          data: this.pieData.map(function (item) {
            return item.docClass || item.departmentName;
          })
        },
        series: [{
          type: 'pie',
          radius: '55%',
          center: ['50%', '60%'],
          data: this.pieData.map(function (item) {
            return {
              value: item.proportion,
              name: item.docClass || item.departmentName
            };
          }),
          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }]
      });
    }
  },
  mounted: function mounted() {
    this.initChart();
  }
};
