import { addProdGroupsDataAPI, getProdGroupsDetailAPI, updateProdGroupsDataAPI } from '@/api/prodGroupAPI.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 规格列表
      pgid: '',
      dataForm: {
        title: '',
        domains: []
      },
      imageShowUrl: '' // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    prodgroupId: {
      required: true
    }
  },
  computed: {},
  methods: {
    /*
      * 获取添加属性组
      */
    pushGroupFormData: function pushGroupFormData() {
      var _this = this;

      this.$refs['dataForm'].validate(function (valid) {
        if (valid) {
          if (_this.dataForm.domains.length < 1) {
            _this.$message({
              type: 'error',
              message: '请添加子分类'
            });

            return false;
          }

          var namesdata = '';

          for (var i = 0; i < _this.dataForm.domains.length; i++) {
            if (i == 0) {
              namesdata += '' + _this.dataForm.domains[i].value;
            } else {
              namesdata += ',' + _this.dataForm.domains[i].value;
            }
          }

          var args = {
            title: _this.dataForm.title,
            names: namesdata
          };

          if (_this.pgid != 0 && _this.pgid != '') {
            args.pgid = _this.pgid;
            updateProdGroupsDataAPI(args).then(function (res) {
              var data = res.data;

              if (data.code === 200) {
                _this.$message({
                  type: 'success',
                  message: '修改属性组成功'
                });

                _this.backhtmls();

                _this.$emit('finish-action');
              } else {
                _this.$message.error(res.data.msg);
              }
            }).catch(function (err) {});
          } else {
            addProdGroupsDataAPI(args).then(function (res) {
              var data = res.data;

              if (data.code === 200) {
                _this.$message({
                  type: 'success',
                  message: '添加属性组成功'
                });

                _this.backhtmls();

                _this.$emit('finish-action');
              } else {
                _this.$message.error(res.data.msg);
              }
            }).catch(function (err) {});
          }
        }
      });
    },
    removeDomain: function removeDomain(item) {
      console.log(123);
      var index = this.dataForm.domains.indexOf(item);

      if (index !== -1) {
        this.dataForm.domains.splice(index, 1);
      }
    },
    addDomain: function addDomain() {
      this.dataForm.domains.push({
        value: '',
        key: Date.now()
      });
    },
    errorMsg: function errorMsg(message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      });
    },
    getGroupDetail: function getGroupDetail(groubid) {
      var _this2 = this;

      this.wholeLoding = true;
      getProdGroupsDetailAPI(groubid).then(function (res) {
        var groupdata = res.data.data;
        _this2.dataForm.title = groupdata.title;
        var groupdatanamelsit = groupdata.names.split(',');

        for (var i = 0; i < groupdatanamelsit.length; i++) {
          _this2.dataForm.domains.push({
            value: groupdatanamelsit[i]
          });
        }

        console.log();
      }).catch(function (err) {
        _this2.wholeLoding = false;
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    if (this.prodgroupId != 0) {
      this.pgid = this.prodgroupId;
      this.getGroupDetail(this.prodgroupId);
    }
  }
};
