import {
  getFranchiseDetailAPI,
  pushFranchiseDetailAPI,
  putFranchiseDetailAPI
} from '@/api/franchiseStoreAdministrators.js'
export default {
   name:'editor',
  data () {
    return {
      // 规格列表
      dataForm:{
        username :'',
        pwd :'',
        roleId:3,
        nickname:'',
        cover:'',
        phone:''
      },
      ispwdshow:0,
      imageShowUrl:''
      // resourcesUrl: window.SITE_CONFIG.resourcesUrl
    }
  },
  components: {

  },
  props: {
    franchiseId: {
      required: true,
    },
  },
  computed: {
    defalutSku () {
      return this.$store.state.prod.defalutSku
    }
  },
  activated () {
    this.dataForm.prodId = this.$route.query.prodId
    this.getDataList()
  },
  methods: {
    uploadImg(res){
      this.dataForm.cover = res.data;
      this.imageShowUrl=res.data
    },
    beforeAvatarUpload(file) {
      const isJPG = file.type === 'image/jpeg' || file.type === 'image/png';
      const isLt2M = file.size / 1024 / 1024 < 2;

      if (!isJPG) {
        this.$message.error('上传头像图片只能是 JPG或者PNG 格式!');
      }
      if (!isLt2M) {
        this.$message.error('上传头像图片大小不能超过 2MB!');
      }
      return isJPG && isLt2M;
    },
    dataFormSubmit () {
        this.$refs['dataForm'].validate((valid) => {
          if(valid){
            const params = {
              username :this.dataForm.username,
              trueName:this.dataForm.trueName,
              roleId:3,
              nickname:this.dataForm.nickname,
              cover:this.dataForm.cover,
              phone:this.dataForm.phone
            }
            if(this.ispwdshow==0){
              params.pwd=this.dataForm.pwd
              pushFranchiseDetailAPI(params)
                .then(res => {
                    const sucresponse = res.data;
                    if(sucresponse.code==200){
                      this.$message({
                        type: 'success',
                        message: '添加管理员信息成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(sucresponse.message)
                    }
                })
                .catch(err => {
                  console.log(err)
                    this.tableLoading = false;
                })
            }else{
              params.uid=this.dataForm.uid
              putFranchiseDetailAPI(params)
                .then(res => {
                    const sucresponse = res.data;
                    if(sucresponse.code==200){
                      this.$message({
                        type: 'success',
                        message: '修改管理员信息成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(sucresponse.message)
                    }
                })
                .catch(err => {
                  console.log(err)
                    this.tableLoading = false;
                })
            }

            console.log(params)
          }
        })
    },
    getoneTypeList(id){

    },
    getFranchiseDetail(id){
          const params =id
            getFranchiseDetailAPI(params)
                .then(res => {
                    console.log(res)
                    const data=res.data.data
                    this.dataForm=data
                    this.imageShowUrl=data.cover
                })
                .catch(err => {
                    this.tableLoading = false;
                })
    },
    errorMsg (message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      })
    },
    /*
      * 返回上层
      */
    backhtmls(){
        this.$emit('close-form')
    }    
  },
  mounted() {
    if(this.franchiseId!=0){
      this.getFranchiseDetail(this.franchiseId)
      this.ispwdshow=this.franchiseId
    }
      console.log(this.franchiseId)
  },
}
