const SecondIndex = () => import('./adminInfo.vue');
const RoleIndex = () => import('./adminRoleSet.vue');
import {
    getAdminListAPI,
    deleteAdminAPI
} from '@/api/adminApi.js'

export default {
    name:'prodList',
    data(){
        return{
            form:{
                size:10,
                current:1,
            },
            tableData:[],
            totalCount:0,
            tableLoading:false,
            isEdit:false,//是否编辑
            adminId:0,//管理员ID
            secondDepShow:false,//管理员添加修改弹窗
            setRoleDepShow:false//设置角色弹窗
        };
    },
    components:{
        // AddDepModal,
        SecondIndex,
        RoleIndex
    },
    methods:{
        /*
         * 获取管理员分类列表
         */
        getList() {
            const params = {...this.form};

            this.tableLoading = true;
            getAdminListAPI(params)
                .then(res => {
                    const data = res.data.data;
                    console.log("getAdminListAPI")
                    console.log(res)
                    this.totalCount = data.length;
                    this.tableData = data;
                    // this.tableLoading = false;
                })
                .catch(err => {
                    this.tableLoading = false;
                })
        },

       handleSecondDepShow(){
            this.secondDepShow = true;
            this.depId=0
            this.isEdit=false
        },
        /**
         * @msg: 修改
         * 
         */
        handleChange(changeid) {
            this.adminId = changeid;
            this.isEdit = false;
            this.secondDepShow = true;
        },
         handleSetRole(changeid){
            this.adminId = changeid;
            this.setRoleDepShow = true;
        },
        /**
         * @msg: 删除管理员
         * @param {Number} row  删除产品分类
         */
        handleDel(index){
             this.$confirm('确定进行删除操作?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(() => {
               let delID = {
                uids:index
                }
                deleteAdminAPI(delID)
                    .then(res => {
                        const response = res.data;
                        let status = 'success';

                        if (response.code === 200) {
                            this.getList();
                        } else {
                            status = 'error';
                        }
                        this.$message({
                            type: status,
                            message: status === 'success' ? '删除成功！' : response.msg,
                        })
                    })
            }).catch(() => { })


        },
    },
    created(){
        this.getList()
    }
}
