import {
  getAdminRoleInfoAPI,
  pushAdminRoleInfoAPI,
  putAdminRoleInfoAPI
} from '@/api/adminRoleApi.js'
export default {
   name:'editor',
  data () {
    return {
      // 规格列表
      dataForm:{
        roleName :'',
        note :''
      }
    }
  },
  components: {

  },
  props: {
    updateId: {
      required: true,
    },
  },
  computed: {
    defalutSku () {
      return this.$store.state.prod.defalutSku
    }
  },
  activated () {
    this.dataForm.prodId = this.$route.query.prodId
    this.getDataList()
  },
  methods: {

    dataFormSubmit () {
        this.$refs['dataForm'].validate((valid) => {
          if(valid){
            const params = {
              roleName :this.dataForm.roleName,
              note:this.dataForm.note        
            }
            if(this.updateId==0){

              pushAdminRoleInfoAPI(params)
                .then(res => {
                    const sucresponse = res.data;
                    if(sucresponse.code==200){
                      this.$message({
                        type: 'success',
                        message: '添加成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(sucresponse.message)
                    }
                })
                .catch(err => {
                  console.log(err)
                    this.tableLoading = false;
                })
            }else{
              params.id=this.updateId
              putAdminRoleInfoAPI(params)
                .then(res => {
                    const sucresponse = res.data;
                    if(sucresponse.code==200){
                      this.$message({
                        type: 'success',
                        message: '修改成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(sucresponse.message)
                    }
                })
                .catch(err => {
                  console.log(err)
                    this.tableLoading = false;
                })
            }

            console.log(params)
          }
        })
    },

    getDetail(id){
          const params =id
            getAdminRoleInfoAPI(params)
                .then(res => {
                    console.log(res)
                    const data=res.data.data
                    this.dataForm=data
                })
                .catch(err => {
                    this.tableLoading = false;
                })
    },
    errorMsg (message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      })
    },
    /*
      * 返回上层
      */
    backhtmls(){
        this.$emit('close-form')
    }    
  },
  mounted() {
    if(this.updateId!=0){
      this.getDetail(this.updateId)
    }
      console.log(this.updateId)
  },
}
