const SecondIndex = () => import('./agentInformationInfo.vue');
const ThreeIndex = () => import('./agentyetz.vue');
import {
     getAgentInfoListAPI,
     delAngetuserAPI
} from '@/api/agentInformationApi.js'
import FileSaver from 'file-saver'

import XLSX from 'xlsx'
export default {
    name:'prodList',
    data(){
        return{
            form:{
                size:10,
                current:1,
                search: '',
                // statusType:'1'
            },
            agentId:'',
            reviewedList:{
                1:'审核通过',
                0:'未通过'
            },
            value1:[,],
            shoptypelist:[{name:'出售中',val:'1',nownum:''},{name:'已下架',val:'2',nownum:''},{name:'库存预警',val:'3',nownum:''}],
            shoptype:1,
            shopClassification:'',
            ommodityType:'',
            searchshow:false,
            keyword:"",
            tableData:[],
            totalCount:0,
            tableLoading:false,
            secondDepShow:false,
            threeDepShow:false
        };
    },
    components:{
        SecondIndex,
        ThreeIndex
    },
    methods:{

        /**
         * @msg: 翻页
         * @param {type} 
         * @return: 
         */
        handleSizeChange(cSize) {
            this.form.size = cSize;
            this.getStockList();
        },
        changeshow(){
            console.log(123)
            this.searchshow=!this.searchshow
        },
        delangetuser(id){
            this.$confirm('确定要删除该用户么？（此操作不可逆）', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            })
                .then(() => {
                    delAngetuserAPI(id)
                    .then(res => {
                        if(res.data.code === 200){
                            this.$message({
                                type: 'success',
                                message: '该用户已经删除',
                            });
                            this.getStockList()
                        }else{
                            this.$message.error(res.data.msg)
                        }                   
                    })
                    .catch(err => {
                    })
                })
        },
        // 导出表格
        exportExcel () {
             /* generate workbook object from table */
            var xlsxParam = { raw: true } // 导出的内容只做解析，不进行格式转换
            var wb = XLSX.utils.table_to_book(document.querySelector('#exportTab'), xlsxParam)

            /* get binary string as output */
            var wbout = XLSX.write(wb, { bookType: 'xlsx', bookSST: true, type: 'array' })
            try {
                FileSaver.saveAs(new Blob([wbout], { type: 'application/octet-stream' }), '代理信息表.xlsx')
            } catch (e) {
                if (typeof console !== 'undefined') {
                console.log(e, wbout)
                }
            }
            return wbout
        },
        changeagentdetail(id){
            this.secondDepShow=true
            this.agentId=id
        },
        changeagentye(id){
            console.log('点击修改')
            this.threeDepShow=true
            this.agentId=id
        },
        handleCurrentChange(currentPage) {
            this.form.current = currentPage;
            this.getStockList();
        },
        getStockList(){
            const params = {...this.form};
            this.tableLoading = true;
             getAgentInfoListAPI(params)
                .then(res => {
                    const data = res.data.data;
                    this.totalCount = data.total;
                    this.tableData = data.records;
                    this.tableLoading = false;
                })
                .catch(err => {
                    this.tableLoading = false;
                })
        }
    },
    created(){
        this.getStockList()
    }
}
