const LineChart = () => import('../charts/LineChart.vue');
const PieChart = () => import('../charts/PieChart.vue');

import {
    getDoctorCountAPI,
    getDoctorDataAPI,
} from '@/api/homeAPI.js';

export default {
    name:'',
    data() {
        return {
            loading: true,
            year: 2018,
            hospitalClass: '三甲',
            lineXAxisData: [],
            lineData: [],
            // 饼图数据
            signData: [], // 签约占比
            doctorClassData: [], // 医生占比
            departmentData: [], // 科室医生占比
        };
    },
    components: {
        LineChart,
        PieChart,
    },
    methods: {
        /**
         * 返回首页
         */
        backOverview() {
            this.$emit('back-overview', '');
        },
        /**
         * 获取医生折线图数据
         */
        getDoctorCount() {
            return getDoctorCountAPI({
                    year: this.year,
                    hospitalClass: this.hospitalClass,
                })
                    .then(res => {
                        const data = res.data.data;
                        let lineXAxisData = [];
                        let lineData = [];

                        data.forEach(item => {
                            lineXAxisData.push(`${item.month}月`);
                            lineData.push(item.doctorNum);
                        })
                        this.lineXAxisData = lineXAxisData;
                        this.lineData = lineData;
                    });
        },
        /**
         * 获取医生数据饼图数据
         */
        getDoctorData() {
            return getDoctorDataAPI({
                    year: this.year,
                    hospitalClass: this.hospitalClass,
                })
                    .then(res => {
                        const data = res.data.data;

                        this.signData = data.sign;
                        this.doctorClassData = data.doctorClass;
                        this.departmentData = data.department;
                    });;
        },
        /**
         * 根据筛选选择器获取新数据
         */
        filtData() {
            this.loading = true;
            Promise.all([
                this.getDoctorCount(),
                this.getDoctorData(),
            ])
                .then(res => {
                    this.loading = false;
                })
                .catch(err => {
                    this.loading = false;
                });
        },
    },
    created() {
        this.filtData();
    },
}
