// const SecondIndex = () => import('./paymentAuditInfo.vue');
import {
     getAgentDepListAPI,
     checkagentOkAPI,
     checkagentNoAPI
} from '@/api/agentAduitAPI.js'
export default {
    name:'prodList',
    data(){
        return{
            form:{
                current:1,
                size:10,
            },
            paymentId:'',
            secondDepShow:false,
            statusList:{
                0:"未审核",
                1:"资料审核",
                2:"付款审核",
                3:"审核通过",
                4:"已拒绝"
            },
            reviewStateList:{
                0:'待审核',
                1:'审核通过',
                2:'已拒绝'
            },
            notext:'',
            noid:'',
            shoptypelist:[{name:'资料审核',val:'1',nownum:''},{name:'付款审核',val:'2',nownum:''},{name:'完成审核',val:'3',nownum:''},{name:'已拒绝',val:'4',nownum:''},{name:'全部申请',val:'5',nownum:''}],
            shoptype:1,
            searchshow:false,
            keyword:"",
            tableData:[],
            totalCount:0,
            tableLoading:false,
            modelExamine: false,
        };
    },
    components:{
        // SecondIndex
    },
    methods:{

        /**
         * @msg: 翻页
         * @param {type} 
         * @return: 
         */
        handleSizeChange(cSize) {
            this.form.size = cSize;
            this.getAgentList();
        },
        closeExamineForm(){
            this.modelExamine=false
            this.notext=''
            this.noid=''
        },
        paymentAuditIsOk(id){
            const params={
                id:id,
            }
            checkagentOkAPI(params)
                .then(res => {
                    const response = res.data;
                    if(response.code==200){
                      this.$message({
                        type: 'success',
                        message: '通过审核成功',
                      });
                      this.getAgentList()
                    }else{
                      this.errorMsg(response.message)
                    }
                })
                .catch(err => {
                    this.tableLoading = false;
                })
            console.log(params)
        },
        checnoOk(){
            const params={
                id:this.noid,
                refuseReason:this.notext
            }
            checkagentNoAPI(params)
                .then(res => {
                    const response = res.data;
                    if(response.code==200){
                      this.$message({
                        type: 'success',
                        message: '已驳回该审核',
                      });
                      this.modelExamine=false
                    }else{
                      this.errorMsg(response.message)
                    }
                })
                .catch(err => {
                    this.tableLoading = false;
                })
        },
        errorMsg (message) {
            this.$message({
                message: message,
                type: 'error',
                duration: 1500
            })
        },
        paymentAuditIsNO(id){
            this.modelExamine=true
            this.noid=id
        },
        handleChangePatmentDetail(id){
            console.log(id)
            this.paymentId=id;
            this.secondDepShow=true
        },
        changeshow(){
            console.log(123)
            this.searchshow=!this.searchshow
        },
        handleCurrentChange(currentPage) {
            this.form.current = currentPage;
            this.getAgentList();
        },
        getAgentList(){
            const params = {...this.form};
            this.tableLoading = true;
             getAgentDepListAPI(params)
                .then(res => {
                    const data = res.data.data;
                    this.totalCount = data.total;
                    this.tableData = data.records;
                    this.tableLoading = false;
                })
                .catch(err => {
                    this.tableLoading = false;
                })
        }
    },
    created(){
        this.getAgentList()
    }
}
