import {
  getAgentDetailAPI,
  changeyeAPI
} from '@/api/agentInformationApi.js'
export default {
   name:'editor',
  data () {
    const validatePass = (rule, value, callback) => {
      console.log(this.dataForm.num)
      if(Number(this.dataForm.num)>Number(this.allnum)&&this.dataForm.changeType==2){
         callback(new Error('调整数目不能大于余额'))
      }else{
        callback()
      }
    }
    return {
      // 规格列表
      Stocktype:[{val:1,title:'增加积分'},{val:2,title:'减少积分'}],
      allnum:'0',
      rules:{
        num: [
          { required: true, message: '不能为空', trigger: ['blur', 'change'] },
           { required: true, trigger: 'blur', validator: validatePass }
        ],
      },
      changeid:'',
      userdetial:{},
      dataForm:{
        changeType:1,
        num:''
      },
      // resourcesUrl: window.SITE_CONFIG.resourcesUrl
    }
  },
  components: {

  },
  props: {
    agentId: {
      required: true,
    },
  },
  computed: {
    defalutSku () {
      return this.$store.state.prod.defalutSku
    }
  },
  methods: {
    dataFormSubmit () {
        this.$refs['dataForm'].validate((valid) => {
          if(valid){
            const params ={
              agentId :this.changeid,
              adminId:sessionStorage.getItem('uid'),
              changeType:this.dataForm.changeType,
              amount:this.dataForm.num
            }
            changeyeAPI(params)
                .then(res => {
                    const response = res.data;
                    console.log(response)
                    if(response.code==200){
                      this.$message({
                        type: 'success',
                        message: '修改积分成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(response.message)
                    }
                })
                .catch(err => {
                    this.tableLoading = false;
                })
          }
        })
    },
    getAgentDetail(id){
      console.log(id)
       getAgentDetailAPI(id)
        .then(res => {
          const data = res.data.data;
          this.allnum =data.incomeBalance
          this.userdetial=data
          console.log(data)
        })
        .catch(err => {
            this.tableLoading = false;
        })
    },
    errorMsg (message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      })
    },
    /*
      * 返回上层
      */
    backhtmls(){
        this.$emit('close-form')
    }    
  },
  mounted() {
    this.getAgentDetail(this.agentId)
    this.changeid =this.agentId
  },
}
