const SecondIndex = () => import('./adminRoleInfo.vue');
const PowerIndex = () => import('./adminRoleSetPower.vue');
import {
    getAdminRoleListAPI,
    deleteAdminRoleAPI
} from '@/api/adminRoleApi.js'

export default {
    name:'prodList',
    data(){
        return{
            form:{
                size:10,
                current:1,
            },
            tableData:[],
            totalCount:0,
            tableLoading:false,
            isEdit:false,//是否编辑
            updateId:0,//修改ID
            secondDepShow:false,//管理员添加修改弹窗
            setPowerDepShow:false,//设置权限弹窗
            roleId:0
        };
    },
    components:{
        // AddDepModal,
        SecondIndex,
        PowerIndex
    },
    methods:{
        /*
         * 获取管理员分类列表
         */
        getList() {
            const params = {...this.form};

            this.tableLoading = true;
            getAdminRoleListAPI(params)
                .then(res => {
                    const data = res.data.data;
                    console.log(data)
                    this.totalCount = data.length;
                    this.tableData = data;
                    // this.tableLoading = false;
                })
                .catch(err => {
                    this.tableLoading = false;
                })
        },
         /**
         * @msg: 修改
         * 
         */
        handleChange(changeid) {
            this.updateId = changeid;
            this.isEdit = false;
            this.secondDepShow = true;
        },
       handleSecondDepShow(){
            this.secondDepShow = true;
            this.depId=0
            this.isEdit=false
        },
       handleSetPower(changeid){
            this.roleId = changeid;
            this.setPowerDepShow = true;
        },


        /**
         * @msg: 删除
         * 
         */
        handleDel(index){
            this.$confirm('确定进行删除操作?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(() => {
               let delID = {
                ids:index
            }
            deleteAdminRoleAPI(delID)
                .then(res => {
                    const response = res.data;
                    let status = 'success';

                    if (response.code === 200) {
                        this.getList();
                    } else {
                        status = 'error';
                    }
                    this.$message({
                        type: status,
                        message: status === 'success' ? '删除成功！' : response.msg,
                    })
                })
            }).catch(() => { })


        },
    },
    created(){
        this.getList()
    }
}
