const LineChart = () => import('../charts/LineChart.vue');

import {
    getPatientCountAPI
} from '@/api/homeAPI.js';

export default {
    name:'',
    data() {
        return {
            loading: true,
            year: 2018,
            lineXAxisData: [],
            lineData: [],
        };
    },
    components: {
        LineChart,
    },
    methods: {
        /**
         * 返回首页
         */
        backOverview() {
            this.$emit('back-overview', '');
        },
        /**
         * 获取患者折线图数据
         */
        getPatientCount() {
            this.loading = true;

            return getPatientCountAPI({
                    year: this.year
                })
                    .then(res => {
                        const data = res.data.data;
                        let lineXAxisData = [];
                        let lineData = [];

                        data.forEach(item => {
                            lineXAxisData.push(`${item.month}月`);
                            lineData.push(item.patientNum);
                        })
                        this.lineXAxisData = lineXAxisData;
                        this.lineData = lineData;
                        this.loading = false;
                    })
                    .catch(err => {
                        this.loading = false;
                    });
        },
    },
    created() {
        this.getPatientCount();
    },
}
