import {
  getAdminRoleListAPI,
} from '@/api/adminRoleApi.js'

import {
  updateAdminRoleAPI
} from '@/api/adminApi.js'

export default {
   name:'editor',
  data () {
    return {
      // 规格列表

      dataForm:{
        roleId:0,
        roleName:''
      },
      roleList:[],
    }
  },
  components: {

  },
  props: {
    adminId: {
      required: true,
    },
  },

  methods: {

    dataFormSubmit () {
      console.log("adminId:"+this.adminId+" roleId:"+this.dataForm.roleId);
        this.$refs['dataForm'].validate((valid) => {
          if(valid){
            const params = {
              adminId:this.adminId,
              roleId:this.dataForm.roleId
            }

              updateAdminRoleAPI(params).then(res => {
                    const sucresponse = res.data;
                    if(sucresponse.code==200){
                      this.$message({
                        type: 'success',
                        message: '设置成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(sucresponse.message)
                    }
                })
                .catch(err => {
                  console.log(err)

                })


            console.log(params)
          }
        })
    },

    getAdminRoleList(){
      console.log("getAdminRoleList adminId:"+this.adminId);
            getAdminRoleListAPI()
                .then(res => {
                    const data = res.data.data;
                    console.log(data)
                    this.roleList = data;
                    console.log(roleList)
                })
                .catch(err => {

                })
    },

    errorMsg (message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      })
    },
    /*
      * 返回上层
      */
    backhtmls(){
        this.$emit('close-form')
    }

  },

   mounted() {
      console.log("mounted方法");
      this.getAdminRoleList();  
    },  

}
