import {
  getProdDetailAPI,
  changeProdDetilAPI,
  changeSkuDetilAPI
} from '@/api/productStockApi.js'
export default {
   name:'editor',
  data () {
    const validatePass = (rule, value, callback) => {
      console.log(this.dataForm.num)
      if(Number(this.dataForm.num)>Number(this.allnum)&&this.dataForm.changeType==2){
         callback(new Error('调整数目不能大于库存'))
      }else{
        callback()
      }
    }
    return {
      // 规格列表
      Stocktype:[{val:1,title:'增加库存'},{val:2,title:'减少库存'}],
      allnum:'0',
      prodName:'',
      cover:'',
      rules:{
        num: [
          { required: true, message: '不能为空', trigger: ['blur', 'change'] },
           { required: true, trigger: 'blur', validator: validatePass }
        ],
      },
      skuId:'',
      dataForm:{
        pid:'',
        changeType:1,
        userType:1,
        operatorId:1,
        remark:'',
        num:''
      },
      // resourcesUrl: window.SITE_CONFIG.resourcesUrl
    }
  },
  components: {

  },
  props: {
    prodId: {
      required: true,
    },
    prodNum: {
      required: true,
    },
    prodSku: {
      required: true,
    },
  },
  computed: {
    defalutSku () {
      return this.$store.state.prod.defalutSku
    }
  },
  methods: {
    dataFormSubmit () {
        this.$refs['dataForm'].validate((valid) => {
          if(valid){
            const params =this.dataForm
            if(this.skuId!=''){
              console.log('修改sku库存')
              let pushparams={
                pid:params.pid,
                changeType:params.changeType,
                remark:params.remark,
                userType:1,
                operatorId:1,
                skuStockDtos:[{
                  skuId:this.skuId,
                  num:Number(params.num)
                }]
              }
              changeSkuDetilAPI(pushparams)
                .then(res => {
                    const response = res.data;
                    console.log(response)
                    if(response.code==200){
                      this.$message({
                        type: 'success',
                        message: '修改库存成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(response.message)
                    }
                })
                .catch(err => {
                    this.tableLoading = false;
                }) 
            }else{
              params.num=Number(params.num)
              changeProdDetilAPI(params)
                .then(res => {
                    const response = res.data;
                    if(response.code==200){
                      this.$message({
                        type: 'success',
                        message: '修改库存成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      errorMsg(response.message)
                    }
                })
                .catch(err => {
                    this.tableLoading = false;
                })     
            }
          }
        })
    },
    getProdDetail(id){
       getProdDetailAPI(id)
        .then(res => {
          const data = res.data.data;
          this.prodName=data.productName
          this.cover=data.cover
          console.log(data)
        })
        .catch(err => {
            this.tableLoading = false;
        })
    },
    errorMsg (message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      })
    },
    /*
      * 返回上层
      */
    backhtmls(){
        this.$emit('close-form')
    }    
  },
  mounted() {
    this.dataForm.pid=this.prodId
    this.getProdDetail(this.prodId)
    this.allnum=this.prodNum
    if(this.prodSku!=0){
      this.skuId=this.prodSku
    }
    console.log(this.prodSku)
    console.log(this.prodNum)
  },
}
