import {
    getPayeeAccountsInfoAPI,
    updatePayeeAccountsInfoAPI
} from '@/api/financeAPI.js'
export default {
   name:'editor',
  data () {
    return {
      // 规格列表
      pgid:'',
      options: [
        {
          value: 1,
          label: '银行卡'
        },{
          value: 2,
          label: '支付宝'
        },{
          value: 3,
          label: '微信'
        }
      ],
      dataForm:{
        title:'',
        domains: [],
        type:'',
        cover:''
      },
      imageShowUrl:''
      // resourcesUrl: window.SITE_CONFIG.resourcesUrl
    }
  },
  components: {

  },
  props: {
    financeId: {
      required: true,
    },
  },
  computed: {
  },
  methods: {
    errorMsg (message) {
      this.$message({
        message: message,
        type: 'error',
        duration: 1500
      })
    },
    uploadImg(res){
      this.dataForm.qrCode = res.data;
      this.imageShowUrl=res.data
    },
    beforeAvatarUpload(file) {
      const isJPG = file.type === 'image/jpeg' || file.type === 'image/png';
      const isLt2M = file.size / 1024 / 1024 < 2;

      if (!isJPG) {
        this.$message.error('上传头像图片只能是 JPG或者PNG 格式!');
      }
      if (!isLt2M) {
        this.$message.error('上传头像图片大小不能超过 2MB!');
      }
      return isJPG && isLt2M;
    }, 
    getFinanceSetUpInfo(id){
      getPayeeAccountsInfoAPI(id)
        .then(res => {
          console.log(res)
          const  data =res.data.data
          this.dataForm=data
          console.log(data)
        }).catch(err => {
            this.tableLoading = false;
        })
    },
    dataFormSubmit () {
          let that=this;
        this.$refs['dataForm'].validate((valid) => {
          if(valid){
            const putdata={
              id:this.dataForm.id,
              type:this.dataForm.type,
              trueName:this.dataForm.trueName,
              depositBank:this.dataForm.depositBank,
              qrCode:this.dataForm.qrCode
            }
            updatePayeeAccountsInfoAPI(putdata)
                .then(res => {
                    const sucresponse = res.data;
                    if(sucresponse.code==200){
                      this.$message({
                        type: 'success',
                        message: '修改账户成功',
                      });
                      this.$emit('close-form')
                      this.$emit('finish-action')
                    }else{
                      this.errorMsg(sucresponse.message)
                    }
                })
                .catch(err => {
                  console.log(err)
                    this.tableLoading = false;
                }) 

          }
        })
    },
    /*
      * 返回上层
      */
    backhtmls(){
        this.$emit('close-form')
    }    
  },
  mounted() {
    console.log(this.financeId)
    if(this.financeId!=0){
      // this.pgid=this.prodgroupId;
      this.getFinanceSetUpInfo(this.financeId)
      // this.getGroupDetail(this.prodgroupId)
    }
  },
}
