const SecondIndex = () => import('./prodInfo.vue');
import {
     getProductsDepListAPI,
     putProdShopAPI,
     deleteFirstDepAPI
} from '@/api/productApi.js'
import {
     timeStamp2String
} from '@/api/globalAPI.js'

export default {
    name:'prodList',
    data(){
        return{
            form:{
                current :1,
                size :10,
                productType: 0,
                // search:'',
                // startTime:'',
                // endTime:'',
                // productNumber:'',
                // typeId:'',
                // minStore:'',
                // maxStore:'',
                // minPrice:'',
                // maxPrice:'',
                // minSales:'',
                // maxSales:'',
                statusType:1,
                sortType:1
                // freightId:''
            },
            freightlist:[],
            value1:[, ],
            shoptypelist:[{name:'出售中',val:'1',nownum:''},{name:'已下架',val:'2',nownum:''},{name:'已售完',val:'3',nownum:''},{name:'库存预警',val:'4',nownum:''}],
            shoptype:1,
            shopClassification:'',
            ommodityType:'',
            searchshow:false,
            keyword:"",
            tableData:[],
            totalCount:0,
            tableLoading:false,
            modalAddDepShow:false,//新增科室弹窗
            isEdit:false,//是否编辑
            depId:0,//科室ID
            secondDepShow:false,//产品1弹窗
        };
    },
    components:{
        // AddDepModal,
        SecondIndex
    },
    methods:{
        /*
         * 获取产品列表
         */
        getProdList(){
            const params = {...this.form};
            if(this.value1[0]!=''&&this.value1[0]!=undefined&&this.value1[0]!=null){
                params.endTime=timeStamp2String(this.value1[1],2)
                params.startTime=timeStamp2String(this.value1[0],2)
            }
            this.tableLoading = true;
             getProductsDepListAPI(params)
                .then(res => {
                    const data = res.data.data;
                    if(data!=null){
                        this.totalCount = data.page.total;
                        this.tableData = data.page.records;
                        this.tableLoading = false;
                        let nowprodnum =data.count;
                        this.shoptypelist[0].nownum=nowprodnum.sellNum
                        this.shoptypelist[1].nownum=nowprodnum.soldOutNum
                        this.shoptypelist[2].nownum=nowprodnum.sellOutNum
                        this.shoptypelist[3].nownum=nowprodnum.warningNum
                    }else{
                        this.tableData=[]
                         this.tableLoading = false;
                         this.totalCount = 0;
                    }
                })
                .catch(err => {
                    this.errorMsg(err)
                    this.tableLoading = false;
                })
        },
        changesearchtype(id){
            this.form.statusType=id
            this.getProdList()
        },
        changetable(e){
            if(e.prop=='price'&&e.order=='ascending'){
                this.form.sortType=1
            }else if(e.prop=='price'&&e.order=='descending'){
                this.form.sortType=2
            }
            this.getProdList()
        },
        /**
         * @msg: 翻页
         * @param {type} 
         * @return: 
         */
        handleSizeChange(cSize) {
            this.form.size = cSize;
            this.getProdList();
        },

        handleCurrentChange(currentPage) {
            this.form.current = currentPage;
            this.getProdList();
        },
        /**
         * @msg: 新增产品
         * @param {type} 
         * @return: 
         */
        handleAddDep(){
            this.depId = 0;
            this.isEdit = false;
            this.secondDepShow = true;
        },
        /**
         * @msg: 修改产品
         * @param {Number} row  修改产品的信息
         */
        handleCheck(index, row) {
            this.depId = row.pid;
            this.secondDepShow = true;
            this.isEdit = true;
        },
        /**
         * @msg: 删除产品
         * @param {Number} row  删除产品信息
         */
        handleDel(index, row){
            let delID = ''+row.pid
            const params={
                pids:delID
            }
            deleteFirstDepAPI(params)
                .then(res => {
                    const response = res.data;
                    let status = 'success';
                    if (response.code === 200) {
                        this.getProdList();
                    } else {
                        status = 'error';
                    }
                    this.$message({
                        type: status,
                        message: status === 'success' ? '删除成功！' : response.message,
                    })
                })
        },
        handleputAway(index, row){
            let putid = row.pid
            let putstatus =0
            if(row.status==0){
                putstatus=-1
            }
            const params={
                pid:putid,
                status:putstatus
            }
            putProdShopAPI(params)
                .then(res => {
                    const response = res.data;
                    let status = 'success';                    
                    if (response.code === 200) {
                        this.getProdList();
                    } else {
                        status = 'error';
                    }
                    this.$message({
                        type: status,
                        message: status === 'success' ? '操作成功！' : response.message,
                    })
                })
        },
        changeshow(){
            console.log(123)
            this.searchshow=!this.searchshow
        },
        handleSecondDepShow(){
            this.secondDepShow = true;
            this.depId=0
            this.isEdit=false
        },
        errorMsg (message) {
            this.$message({
                message: message,
                type: 'error',
                duration: 1500
            })
        },
    },
    created(){
        this.getProdList()
    }
}
